import React, {useEffect, useState} from 'react';
import '../Chat.scss';

function Chat({ socket, firstName, room }) {

    const [currentMessage, setCurrentMessage] = useState("");

    const [messageList, setMessageList] = useState([]);


    const sendMessage = async (e) => {
        if (currentMessage !== "") {
            e.preventDefault();
            const messageData = {
                room: room,
                author: firstName,
                message: currentMessage,
                time: new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes(),
            };
            await socket.emit("send_message", messageData);
        }
    };

    useEffect( () => {
        socket.on("receive_message", (data) => {
            setMessageList((list) => [...list, data]);
        })
    }, [socket])

    return (

        <>
        <div class='container' ng-cloak ng-app="chatApp">
        <h1>{room}</h1>
        <div class='chatbox' ng-controller="MessageCtrl as chatMessage">
        <div class='chatbox__user-list'>
        <h1>User list</h1>
        <div class='chatbox__user--active'>
        <p>Jack Thomson</p>
        </div>
        <div class='chatbox__user--busy'>
        <p>Angelina Jolie</p>
        </div>
        <div class='chatbox__user--active'>
        <p>George Clooney</p>
        </div>
        <div class='chatbox__user--active'>
        <p>Seth Rogen</p>
        </div>
        <div class='chatbox__user--away'>
        <p>John Lydon</p>
        </div>
    </div>
    <div class="chatbox__messages" ng-repeat="message in messages">
        <div class="chatbox__messages__user-message">
        <div class="chatbox__messages__user-message--ind-message">
        {messageList.map( (messageContent) => {
            return (
            <>
            <p class="name">{messageContent.firstName}</p>
            <p class="message">{messageContent.message}</p>
            </>
            )
        })}
        </div>
        </div>
    </div>
    <form>
        <input type="text" placeholder="Enter your message" onChange={(e) => {setCurrentMessage(e.target.value);}}/>
        <button onClick={sendMessage}>Send</button>
    </form>
    </div>
    </div>
        
    </>
    )

}

export default Chat;
