import React, {useEffect, useState} from 'react';
import '../Chat.scss';
import ScrollToBottom from 'react-scroll-to-bottom';

function Chat({ socket, firstName, room }) {

    const [currentMessage, setCurrentMessage] = useState("");

    const [messageList, setMessageList] = useState([]);


    const sendMessage = async (e) => {
        if (currentMessage !== "") {
            const messageData = {
                room: room,
                author: firstName,
                message: currentMessage,
                time: new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes(),
            };
            await socket.emit("send_message", messageData);
            setMessageList((list) => [...list, messageData]);
            setCurrentMessage("");
        }
    };

    useEffect( () => {

        socket.on("receive_message", (data) => {
            setMessageList((list) => [...list, data]);
        })
    }, [])

    return (

        <> 
<div class='container' ng-cloak ng-app="chatApp">
        <div class='chatbox__user-list'>
        <h1>User list</h1>
        <div class='chatbox__user--active'>
        <p>Jack Thomson</p>
        </div>
        <div class='chatbox__user--busy'>
        <p>Angelina Jolie</p>
        </div>
        <div class='chatbox__user--active'>
        <p>George Clooney</p>
        </div>
        <div class='chatbox__user--active'>
        <p>Seth Rogen</p>
        </div>
        <div class='chatbox__user--away'>
        <p>John Lydon</p>
        </div>
    </div>
    </div>
        <div className="chat-window">
    <div className="chat-header">
        <p>{room}</p>
    </div>
    
    <div className="chat-body">
        <ScrollToBottom className="message-container">
        {messageList.map((messageContent) => {
            return (
            <div
                className="message"
                id={firstName === messageContent.author ? "you" : "other"}
            >
                <div>
                <div className="message-content">
                    <p>{messageContent.message}</p>
                </div>
                <div className="message-meta">
                    <p id="time">{messageContent.time}</p>
                    <p id="author">{messageContent.author}</p>
                </div>
                </div>
            </div>
            );
        })}
        </ScrollToBottom>
    </div>
    <div className="chat-footer">
        <input
        type="text"
        value={currentMessage}
        placeholder="Hey..."
        onChange={(event) => {
            setCurrentMessage(event.target.value);
        }}
        onKeyPress={(event) => {
            event.key === "Enter" && sendMessage();
        }}
        />
        <button onClick={sendMessage}>&#9658;</button>
    </div>
    </div>

    </>
    )

}

export default Chat;

