import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { Link, useNavigate, useParams } from 'react-router-dom';

const ViewProfile = () => {

    const { id } = useParams();

    const [firstName, setFirstName] = useState("");

    const [lastName, setLastName] = useState("");

    const [gender, setGender] = useState("");

    const [image, setImage] = useState("");

    const [errors, setErrors] = useState({});

    const navigate = useNavigate();

    useEffect( () => {
        axios.get(`http://localhost:8000/api/users/${id}`)
            .then( res => {
                setFirstName(res.data.firstName);
                setLastName(res.data.lastName);
                setGender(res.data.gender);
                setImage(res.data.image);
            })
            .catch( (err) => console.log(err))
    }, [])

    const updateProfile = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:8000/api/users/${id}`, {firstName, lastName})
            .then( (res) => {
                console.log(res.data);
                if (res.data.error) {
                    setErrors(res.data.error.errors);
                } else {
                    navigate(`/home`);
                }
            })
    }

    return (
        <>
        
        <form onSubmit={updateProfile}>

        <div>
            <label>Profile Pic</label>
            <input value={image} onChange={(e) => setImage(e.target.value)} type="text"/>
        </div>
        
        <div>
            <label>First Name: </label>
            <input type="text" value={firstName} onChange={(e) => {setFirstName(e.target.value)}} />
            {errors.firstName ? <p>{errors.name.message}</p> : null}
        </div>

        <div>
            <label>Gender: </label>
            <select value={gender} onChange={(e) => {setGender(e.target.value)}} name="gender">
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Agender">Agender</option>
                <option value="Cisgender">Cisgender</option>
                <option value="Genderfluid">Genderfluid</option>
                <option value="Genderqueer">Genderqueer</option>
                <option value="Intersex">Intersex</option>
                <option value="Gender Nonconforming">Gender Nonconforming</option>
                <option value="Transgender">Transgender</option>
            </select>
        </div>

        <input type="submit" value="Update" />

        </form>
        
        </>
    )
}

export default ViewProfile;
